arguments.argument:
if place_free(x,y+yspeed)
{
    if place_free(x,y+1)
        yspeed+=1;
}
else
{
    move_contact_solid(270,yspeed);
    yspeed=0;
}

y+=yspeed;

if place_meeting(x,y+1,obj_kill_no_body)
{
    image_yscale-=.03;
    y+=1;
    move_contact_solid(270,8);
    if (image_yscale<0)
    {
        instance_destroy();
    }
}

if !place_free(x,y)
{
    y-=1;
}

if (yspeed>11+5*image_xscale)
{
    yspeed=11+5*image_xscale;
}
